//导入express模块
var express=require("express")
//导入utility模块
var utility=require("utility");

//建立express实例
var app=express();

app.get("/",function(req,res){
	//取出传递过来的code 比如 127.0.0.1/?code="123" 
	var q=req.query.code;
	console.log(req)
	//md5加密
	var md5V=utility.md5(q)
	//发送加密后的代码
	res.send(md5V)
})

app.listen("3000",function(req,res){
	console.log("run")
})
